;; **
;;; Now let's convert Plato's value into Specific Gravity:
;;; 
;;; formula: SG = 1+ (plato / (258.6 â€“ ( (plato/258.2) *227.1) ) )
;; **

;; @@
  (t/table-view ((an/with-csv an/recipe-file) 
	 	  (fn [rows] (->> rows
                   rest
                   (take 500)
                   (filter #(= "Plato" (nth % 16)))
                   (map #(list (nth % 8) (nth % 7)))
                   doall))) :columns ["OG" "FG"])
